import { createSlice, configureStore } from "@reduxjs/toolkit";
import storage from "redux-persist/lib/storage";
import { persistReducer } from "redux-persist";
import { combineReducers } from "@reduxjs/toolkit";

const persistConfig = {
  key: "root",
  version: 1,
  storage,
};
const initialAuthState = { loggedIn: false, role: null };

const authSlice = createSlice({
  name: "auth",
  initialState: initialAuthState,
  reducers: {
    login(state, action) {
      state.loggedIn = true;
      state.role = action.payload;
    },
    logout(state, action) {
      state.loggedIn = false;
      state.role = action.payload;
    },
  },
});

const initialCartState = {
  cartList: [],
};

const cartSlice = createSlice({
  name: "cart",
  initialState: initialCartState,
  reducers: {
    addToCart(state, action) {
      console.log(action.payload);
      const id = Object.keys(action.payload)[0];
      let flag = 1;
      console.log("after flag", state.cartList);
      for (let a = 0; a < state.cartList.length; a++) {
        if (id === Object.keys(state.cartList[a])[0]) {
          console.log("Addddddddddddddddddddddddddd");
          Object.values(state.cartList[a])[0] += 1;
          console.log(Object.values(state.cartList[a])[0]);
          flag = 0;
        }
      }
      if (flag) {
        state.cartList = [...state.cartList, action.payload];
      }

      console.log("in add to cart", state.cartList);
    },
    removefromcart(state, action) {
      const id = action.payload[0];
      for (let a = 0; a < state.cartList.length; a++) {
        if (id === state.cartList[a][0]) {
          state.cartList[a][1] -= 1;
        }
        if (state.cartList[a][1] === 0) {
          const firsthalf = state.cartList.slice(0, a);
          const secondhalf = state.cartList.slice(-a);
          state.cartList = firsthalf.concat(secondhalf);
        }
      }
    },
    removeCart(state) {
      state.cartList = [];
    },
  },
});
const reducer = combineReducers({
  cart: cartSlice.reducer,
});
const persistedReducer = persistReducer(persistConfig, reducer);
const store = configureStore({
  reducer: { auth: authSlice.reducer, cart: persistedReducer },
});

export const authActions = authSlice.actions;
export const cartActions = cartSlice.actions;
export default store;
