import React, { useState } from "react";
import { useEffect } from "react";
import { useLocation, useNavigate, useSearchParams } from "react-router-dom";
import Loading from "../Components/Loading";
import Pagination from "../Components/Pagination";
import Card from "../Components/UI/Card";

function Products() {
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(12);
  const [filter, setfilter] = useSearchParams();
  const params = [];
  for (let entry of filter.entries()) {
    params.push(entry);
  }
  console.log(params, Object.fromEntries([...filter]));
  const navigate = useNavigate();
  const [last, setlast] = useState();
  const [loading, setloading] = useState(true);
  const [productList, setproductList] = useState([]);
  // const [page, setpage] = useState(1);
  const [count, setCount] = useState();
  const [category, setcategory] = useState(
    Object.fromEntries([...filter])["category"]
  );
  const location = useLocation();

  const totalPages = Math.ceil(count / itemsPerPage);

  const handleClick = (event, page) => {
    event.preventDefault();
    setCurrentPage(page);
  };

  const renderPageNumbers = () => {
    const pageNumbers = [];

    if (totalPages <= 5) {
      for (let i = 1; i <= totalPages; i++) {
        pageNumbers.push(
          <li key={i} className={currentPage === i ? "active" : ""}>
            <a href="#" onClick={(event) => handleClick(event, i)}>
              {i}
            </a>
          </li>
        );
      }
    } else {
      let leftSide = currentPage - 1;
      let rightSide = currentPage + 1;

      if (currentPage === 1) {
        rightSide += 2;
      } else if (currentPage === totalPages) {
        leftSide -= 2;
      }

      for (let i = 1; i <= totalPages; i++) {
        if (i === 1 || i === totalPages || (i >= leftSide && i <= rightSide)) {
          if (i === 1 && i !== leftSide) {
            pageNumbers.push(
              // <li key={i} className={currentPage === i ? 'active' : ''}>
              //   <a href="#" onClick={(event) => handleClick(event, i)}>{i}</a>
              // </li>
              <li
                key={i}
                className="page-item"
                onClick={(event) => handleClick(event, i)}
              >
                <span class="page-link">{i}</span>
              </li>
            );
          } else if (i === totalPages && i !== rightSide) {
            pageNumbers.push(
              // <li key={i} className={currentPage === i ? "active" : ""}>
              //   <a href="#" onClick={(event) => handleClick(event, i)}>
              //     {i}
              //   </a>
              // </li>
              <li
                key={i}
                className="page-item"
                onClick={(event) => handleClick(event, i)}
              >
                <span class="page-link">{i}</span>
              </li>
            );
          } else if (
            i !== 1 &&
            i !== totalPages &&
            pageNumbers[pageNumbers.length - 1] !== "..."
          ) {
            pageNumbers.push(
              // <li key={i} className={currentPage === i ? "active" : ""}>
              //   <a href="#" onClick={(event) => handleClick(event, i)}>
              //     {i}
              //   </a>
              // </li>
              <li
                key={i}
                className="page-item"
                onClick={(event) => handleClick(event, i)}
              >
                <span class="page-link">{i}</span>
              </li>
            );
          }
        } else if (pageNumbers[pageNumbers.length - 1] !== "...") {
          pageNumbers.push(
            <li style={{ color: "black" }} key={i}>
              ...
            </li>
          );
        }
        // if (i === 1 || i === totalPages || (i >= leftSide && i <= rightSide)) {
        //   pageNumbers.push(
        //     <li
        //       key={i}
        //       className="page-item"
        //       style={
        //         currentPage === i
        //           ? { color: "red", backgroundColor: "yellow" }
        //           : { color: "black" }
        //       }
        //       onClick={(event) => handleClick(event, i)}
        //     >
        //       <span class="page-link">{i}</span>
        //     </li>
        //   );
        // } else if (pageNumbers[pageNumbers.length - 1] !== "...") {
        //   pageNumbers.push(
        //     <li style={{ color: "black" }} key={i}>
        //       ...
        //     </li>
        //   );
        // }
      }
    }

    return pageNumbers;
  };

  // console.log(params, Object.fromEntries([...filter])["category"]);
  // const onNext = () => {
  //   setpage((page) => page + 1);
  //   document.documentElement.scrollTop = 0;
  // };
  // const onPrev = () => {
  //   setpage((page) => page - 1);
  //   document.documentElement.scrollTop = 0;
  // };
  // const onFirst = () => {
  //   setpage(1);
  //   document.documentElement.scrollTop = 0;
  // };
  // const onLast = () => {
  //   setpage(last);
  //   document.documentElement.scrollTop = 0;
  // };
  useEffect(() => {
    setcategory(Object.fromEntries([...filter])["category"]);
  }, [location]);
  useEffect(() => {
    const url = "https://ecommerceio.onrender.com/productCount/" + category;
    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        setCount(data.count);
        setlast(Math.ceil(data.count / 12));
      })
      .catch((err) => {
        console.log(err);
      });

    const URL = `https://ecommerceio.onrender.com/products?page=${currentPage}&category=${category}`;
    console.log(URL);
    fetch(URL)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data);
        if (data.message === "Product list") {
          console.log(data.products);
          setproductList(data.products);

          setloading(false);
          // if (data.products.length === 0) setpage((page) => page - 1);
        } else {
          navigate("/");
        }
      });
  }, [category, currentPage]);
  // useEffect(() => {}, [navigate, page, category, filter]);
  return (
    <>
      {loading && <Loading />}
      {!loading && (
        <>
          <div style={{ textAlign: "center", padding: "10px" }}>
            <p style={{ color: "black" }}>
              Showing {(currentPage - 1) * 12 + 1} to{" "}
              {(currentPage - 1) * 12 + productList.length} of {count} amazing
              products
            </p>
          </div>
          <div className="container px-4" style={{ color: "black" }}>
            <div className="row gy-5 gx-5">
              {productList.map((prod) => {
                return (
                  <Card
                    title={prod.productname}
                    price={prod.price}
                    description={prod.description}
                    imageURL={prod.imageURLs[0]}
                    id={prod._id}
                    rating={prod.rating}
                    numOfRev={prod.numberOfReviews}
                  />
                );
              })}
            </div>
          </div>

          <div style={{ display: "flex", justifyContent: "center" }}>
            {/* <nav aria-label="Page navigation example">
              <ul class="pagination">
                {page !== 1 && (
                  <li class="page-item" onClick={onFirst}>
                    <span class="page-link" href="#">
                      1
                    </span>
                  </li>
                )}
                {page - 1 !== 1 && page - 1 > 0 && (
                  <li class="page-item" onClick={onPrev}>
                    <span class="page-link" href="#">
                      {page - 1}
                    </span>
                  </li>
                )}
                (
                <li class="page-item">
                  <span
                    class="page-link"
                    href="#"
                    style={{ color: "black", border: "2px solid orange" }}
                  >
                    {page}
                  </span>
                </li>
                )
                {page + 1 < last && (
                  <li class="page-item" onClick={onNext}>
                    <span class="page-link" href="#">
                      {page + 1}
                    </span>
                  </li>
                )}
                {page !== last && (
                  <li class="page-item" onClick={onLast}>
                    <span class="page-link" href="#">
                      {last}
                    </span>
                  </li>
                )}
              </ul>
            </nav> */}
            <div>
              <ul className="pagination">
                {currentPage !== 1 && (
                  // <li>
                  //   <a href="#" onClick={(event) => handleClick(event, 1)}>
                  //     First
                  //   </a>
                  // </li>
                  <li
                    class="page-item"
                    onClick={(event) => handleClick(event, 1)}
                  >
                    <span class="page-link">1</span>
                  </li>
                )}
                {currentPage !== 1 && (
                  // <li>
                  //   <a
                  //     href="#"
                  //     onClick={(event) => handleClick(event, currentPage - 1)}
                  //   >
                  //     Prev
                  //   </a>
                  // </li>
                  <li
                    class="page-item"
                    onClick={(event) => handleClick(event, currentPage - 1)}
                  >
                    <span class="page-link">Prev</span>
                  </li>
                )}
                {renderPageNumbers()}
                {currentPage !== totalPages && (
                  // <li>
                  //   <a
                  //     href="#"
                  //     onClick={(event) => handleClick(event, currentPage + 1)}
                  //   >
                  //     Next
                  //   </a>
                  // </li>
                  <li
                    class="page-item"
                    onClick={(event) => handleClick(event, currentPage + 1)}
                  >
                    <span class="page-link">Next</span>
                  </li>
                )}
                {currentPage !== totalPages && (
                  // <li>
                  //   <a
                  //     href="#"
                  //     onClick={(event) => handleClick(event, totalPages)}
                  //   >
                  //     Last
                  //   </a>
                  // </li>
                  <li
                    class="page-item"
                    onClick={(event) => handleClick(event, totalPages)}
                  >
                    <span class="page-link">Last</span>
                  </li>
                )}
              </ul>
            </div>
          </div>
          <Pagination
            className="pagination-bar"
            currentPage={currentPage}
            totalCount={10}
            pageSize={12}
            onPageChange={(page) => setCurrentPage(page)}
          />
        </>
      )}
    </>
  );
}

export default Products;
