import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import classes from "./Cart.module.css";

function Cart() {
  // const cart = useSelector((state) => state.cart.cartList);
  const cart = localStorage.getItem("cart").split(" ");
  const [productList, setproductList] = useState([]);
  let cartdata = [];
  cart.forEach((c) => {
    cartdata.push({ id: c });
  });
  console.log(cartdata);
  useEffect(() => {
    fetch("http://localhost:8080/cartData", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        cart: cartdata,
      }),
    })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data);
        setproductList(data.productData);
      });
  }, []);

  return (
    <div>
      Here is your cart
      {productList.map((c) => {
        return (
          <div>
            <div class="card mb-3" style={{ "max-width": "540px" }}>
              <div class="row g-0">
                <div className={classes.mycard}>
                  <img
                    className={`fluid col-md-4 ${classes.img}`}
                    src={c.imageURL}
                    alt="productimage"
                  />
                </div>
                <div class="col-md-8">
                  <div class="card-body">
                    <h5 class="card-title">{c.productname}</h5>
                    <p class="card-text">{c.description}</p>
                    <p class="card-text">
                      <small class="text-muted">{c.price}</small>
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default Cart;
