import Title from "./UI/Title";
import TextField from "./UI/TextField";
import Button from "./UI/Button";
import { useState } from "react";

function Signup(props) {
  const [email, setemail] = useState("");
  const [password, setpassword] = useState("");
  const [username, setusername] = useState("");
  const [passwordValid, setpasswordValid] = useState("");
  const [confirmpasswordValid, setconfirmpasswordValid] = useState("");
  const [emailValid, setemailValid] = useState("");
  const [error, setError] = useState("");
  const [usernameValid, setusernameValid] = useState("");

  const onUserNameChangeHandler = (event) => {
    setusername(event.target.value);
    if (event.target.value.length > 5) {
      setusernameValid("valid");
    } else {
      setusernameValid("invalid");
    }
  };
  const onEmailChangeHandler = (event) => {
    setemail(event.target.value);
    if (event.target.value.length > 5) {
      setemailValid("valid");
    } else {
      setemailValid("invalid");
    }
  };
  const onPasswordChangeHandler = (event) => {
    setpassword(event.target.value);
    if (event.target.value.length > 5) {
      setpasswordValid("valid");
    } else {
      setpasswordValid("invalid");
    }
  };
  const onConfirmPasswordChangeHandler = (event) => {
    setpassword(event.target.value);
    if (event.target.value.length > 5) {
      setconfirmpasswordValid("valid");
    } else {
      setconfirmpasswordValid("invalid");
    }
  };
  const onSubmitSignup = () => {
    console.log(email, password, username);
    fetch("http://localhost:8080/signup", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        email: email,
        password: password,
        username: username,
      }),
    })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data);
        if (data.message === "User signed up") {
          props.toggle();
        } else {
          setError(data.message);
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <>
      <Title title="Sign up" />
      {error && <Error title={error} />}
      <TextField
        label="Username"
        onChange={onUserNameChangeHandler}
        req={true}
        valid={usernameValid}
      />
      <TextField
        label="Email"
        onChange={onEmailChangeHandler}
        req={true}
        valid={emailValid}
      />
      <TextField
        label="Password"
        onChange={onPasswordChangeHandler}
        req={true}
        valid={passwordValid}
      />
      <TextField
        label="Confirm Password"
        onChange={onConfirmPasswordChangeHandler}
        req={true}
        valid={confirmpasswordValid}
      />
      <Button onClick={onSubmitSignup} title="Sign up" />
    </>
  );
}

export default Signup;
