import { useState } from "react";
import classes from "./AddReview.module.css";
import Button from "./UI/Button";

function AddReview() {
  const [rating, setrating] = useState(0);
  const onchangeRating = (event) => {
    console.log("newnum", event.target.area - label, event.target.name);
    // setrating(event.target.value);
  };
  const [showform, setshowform] = useState(false);
  const showformToggler = () => {
    setshowform(!showform);
  };
  return (
    <div className={classes.box}>
      <div className="container">
        <Button title="Add a review" onClick={showformToggler} />
        {showform && (
          <div className={classes.form}>
            <div className={classes.inputs}>
              <div className={classes.rating}>
                Select rating
                <div className={classes.stars} onChange={onchangeRating}>
                  <div className={classes.star} onChange={onchangeRating}>
                    <input type="radio" value="1" name="rating" />
                    <ion-icon
                      className={classes.star}
                      name="star"
                      onClick={onchangeRating}
                      area-label="12"
                    ></ion-icon>
                  </div>
                  <div className={classes.star} onChange={onchangeRating}>
                    <input type="radio" value="2" name="rating" />
                    <ion-icon className={classes.star} name="star"></ion-icon>
                  </div>
                  <div className={classes.star} onChange={onchangeRating}>
                    <input type="radio" value="3" name="rating" />
                    <ion-icon className={classes.star} name="star"></ion-icon>
                  </div>
                  <div className={classes.star} onChange={onchangeRating}>
                    <input
                      type="radio"
                      value="4"
                      name="rating"
                      onChange={onchangeRating}
                    />
                    <ion-icon className={classes.star} name="star"></ion-icon>
                  </div>
                  <div className={classes.star}>
                    <input
                      type="radio"
                      value="5"
                      name="rating"
                      onClick={onchangeRating}
                    />
                    <ion-icon className={classes.star} name="star"></ion-icon>
                  </div>
                </div>
              </div>
              <div>
                <div>Add a feedback</div>
                <textarea className={classes.textarea} />
              </div>
            </div>
            <div className="container">
              <Button title="Submit" />
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default AddReview;
