import classes from "./AddForm.module.css";
import Title from "./UI/Title";
import TextField from "./UI/TextField";
import { useState } from "react";
import Button from "./UI/Button";
import validatefield from "../validator/validator";
import { useNavigate } from "react-router-dom";
import Error from "./UI/Error";

function AddForm() {
  const navigate = useNavigate();
  const [productname, setproductname] = useState();
  const [productnamevalid, setproductnamevalid] = useState();
  const productnameChangeHandler = (event) => {
    setproductname(event.target.value);
    setproductnamevalid(
      validatefield(
        {
          required: true,
          maxlength: 10,
          minlength: 2,
        },
        event.target.value
      )
    );
  };
  const [category, setcategory] = useState();
  const [categoryvalid, setcategoryvalid] = useState();
  const categoryChangeHandler = (event) => {
    setcategory(event.target.value);
    setcategoryvalid(
      validatefield(
        {
          required: true,
          maxlength: 10,
          minlength: 2,
        },
        event.target.value
      )
    );
  };
  const [imageurl, setimageurl] = useState();
  const [imageurlvalid, setimageurlvalid] = useState();
  // const imageurlChangeHandler2 = (event) => {
  //   setimageurl(event.target.value);
  //   setimageurlvalid(
  //     validatefield(
  //       {
  //         required: true,
  //         url: true,
  //       },
  //       event.target.value
  //     )
  //   );
  // };
  const [price, setprice] = useState(0);
  const [pricevalid, setpricevalid] = useState();
  const priceChangeHandler = (event) => {
    setprice(event.target.value);
    setpricevalid(
      validatefield(
        {
          required: true,
          isnum: true,
          minValue: 0,
        },
        event.target.value
      )
    );
  };
  const [available, setavailable] = useState();
  const [availablevalid, setavailablevalid] = useState();
  const availableChangeHandler = (event) => {
    setavailable(event.target.value);
    setavailablevalid(
      validatefield(
        {
          required: true,
          isnum: true,
          minValue: 0,
        },
        event.target.value
      )
    );
  };
  const [description, setdescription] = useState();
  const [descriptionvalid, setdescriptionvalid] = useState();
  const descriptionChangeHandler = (event) => {
    setdescription(event.target.value);
    setdescriptionvalid(
      validatefield(
        {
          minLength: 5,
          required: true,
          maxLength: 20,
        },
        event.target.value
      )
    );
  };
  const [error, setError] = useState();
  const onAddForm = () => {
    if (
      productnamevalid !== "valid" ||
      descriptionvalid !== "valid" ||
      imageurlvalid !== "valid" ||
      availablevalid !== "valid" ||
      pricevalid !== "valid"
    ) {
      setError("Invalid values");
      return;
    }
    const token = localStorage.getItem("token");
    // console.log(productname, price, imageurl, description, available);
    fetch("https://ecommerceio.onrender.com/addproduct", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + token,
      },
      body: JSON.stringify({
        productname: productname,
        imageURL: imageurl,
        price: price,
        availablequantity: available,
        description: description,
        category: category,
      }),
    })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        // console.log(data);
        if (data.message === "Product added") {
          navigate("/products");
        } else {
          setError(data.message);
        }
      })
      .catch((err) => {
        // console.log(err);
        setError("Request Failed");
      });
  };
  const [imageUrls, setImageUrls] = useState([""]); // initialize state with one empty string

  const addImageUrl = () => {
    setImageUrls([...imageUrls, ""]); // add another empty string to the array
  };

  const removeImageUrl = (index) => {
    setImageUrls((prevImageUrls) => {
      const updatedImageUrls = [...prevImageUrls];
      updatedImageUrls.splice(index, 1);
      return updatedImageUrls;
    });
  };

  // const imageurlChangeHandler = (e, index) => {
  //   const newImageUrls = [...imageUrls];
  //   newImageUrls[index] = e.target.value;
  //   setImageUrls(newImageUrls);
  // };
  const imageurlChangeHandler = (e, index) => {
    const newImageUrls = [...imageUrls]; // make a copy of the existing array
    newImageUrls[index] = e.target.value; // update the value at the specified index
    setImageUrls(newImageUrls); // update the state with the new array
  };
  return (
    <>
      <section className={classes.box}>
        {error && <Error title={error} />}
        <Title title="Add Product" />
        <TextField
          label="Product Name"
          req={true}
          icon="mail-outline"
          onChange={productnameChangeHandler}
          valid={productnamevalid}
        />
        {/* <TextField
          label="Image URL"
          req={true}
          icon="image-outline"
          onChange={imageurlChangeHandler}
          valid={imageurlvalid}
        /> */}
        {imageUrls.map((imageUrl, index) => (
          <div key={index}>
            <TextField
              label={`Image URL #${index + 1}`}
              req={true}
              icon="image-outline"
              value={imageUrl}
              onChange={(e) => imageurlChangeHandler(e, index)}
            />
            <button type="button" onClick={() => removeImageUrl(index)}>
              Remove
            </button>
          </div>
        ))}
        <button type="button" onClick={addImageUrl}>
          Add More
        </button>
        <TextField
          label="Price"
          req={true}
          icon="pricetag-outline"
          onChange={priceChangeHandler}
          valid={pricevalid}
        />
        <TextField
          label="Category"
          req={true}
          icon="apps-outline"
          onChange={categoryChangeHandler}
          valid={categoryvalid}
        />
        <TextField
          label="Available Quantity"
          req={true}
          icon="add-circle-outline"
          onChange={availableChangeHandler}
          valid={availablevalid}
        />
        <TextField
          label="Product Description"
          req={true}
          icon="information-circle-outline"
          onChange={descriptionChangeHandler}
          valid={descriptionvalid}
        />
        <Button title="Add Product" onClick={onAddForm} />
      </section>
    </>
  );
}

export default AddForm;
