import classes from "./ProductData.module.css";
import ThumbImage from "./ThumbImage";
import { useState } from "react";

function ProductData() {
  const [image, setimage] = useState(
    "https://images.unsplash.com/photo-1567581935884-3349723552ca?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=774&q=80aG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=580&q=80"
  );
  const onImageChangeHandler = (image) => {
    console.log("on change", image);
    setimage(image);
  };
  return (
    <div className="container">
      <div className={`row ${classes.product}`}>
        <div className={`col-md-8 ${classes.img}`}>
          <img className={classes.image} src={image} />
        </div>
        <div className={`col-md-4 ${classes.data}`}>
          <div className={classes.title}>
            <h2>MacBook Pro</h2>
          </div>
          <div>
            <h4>amazin laptppd afafa fs dfw fwe f we wef ewffwfwef fweef</h4>
          </div>
          <div>* * * * *</div>
          <div>
            <h3>$ 45</h3>
          </div>
        </div>
      </div>
      <div className="row">
        <div className={`row col-md-8 ${classes.thumbnail}`}>
          <ThumbImage
            onImageChangeHandler={onImageChangeHandler}
            source="https://images.unsplash.com/photo-1575024357670-2b5164f470c3?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=870&q=80"
          />
          <ThumbImage />
          <ThumbImage />
          <ThumbImage />
          <ThumbImage />
        </div>
        <div>
          <button className={classes.btn}>Add To Cart</button>
        </div>
      </div>
    </div>
  );
}

export default ProductData;
