import Title from "./UI/Title";
import Error from "./UI/Error";
import TextField from "./UI/TextField";
import Button from "./UI/Button";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

function Login() {
  const [email, setemail] = useState("");
  const [emailValid, setemailValid] = useState("");
  const [password, setpassword] = useState("");
  const [passwordValid, setpasswordValid] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  let validatefield = (constraints, value) => {
    console.log("in validate");
    if (constraints.required && value === "") {
      return "Required";
    }
    if (constraints.minLength) {
      if (constraints.minLength > value.length) {
        return (
          "PLease provide atleast " + constraints.minLength + " characters."
        );
      }
    }
    if (constraints.maxLength) {
      if (constraints.maxLength < value.length) {
        return (
          "PLease provide atmost " + constraints.maxLength + " characters."
        );
      }
    }

    if (constraints.regexp) {
      let pattern = constraints["regexp"];
      if (!pattern.test(value)) {
        return "Invalid value";
      }
    }
    if (constraints.minValue) {
      if (constraints.minValue > value) {
        return "Value should be greater than " + constraints.minValue;
      }
    }
    if (constraints.maxValue) {
      if (constraints.maxValue < value) {
        return "Value should be lesser than " + constraints.maxValue;
      }
    }

    if (constraints.email) {
      if (!/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(value)) {
        return "Invalid Email";
      }
    } else if (constraints.url) {
      if (
        !/(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/g.test(
          value
        )
      ) {
        return "Passwords mismatched";
      }
    } else if (constraints.password) {
      if (
        !/^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{6,16}$/.test(value)
      ) {
        return "Password must have a capital letter, small letter, numerical value ,special character and must be 6 to 16 characters long.";
      }
    }
    if (constraints.matchWith) {
      if (constraints.matchWith !== value) {
        return "Passwords mismatched";
      }
    }
    return "valid";
  };

  const onEmailChangeHandler = (event) => {
    setemail(event.target.value);
    setemailValid(
      validatefield(
        {
          required: true,
          maxLength: 50,
          email: true,
        },
        event.target.value
      )
    );
  };
  const onPasswordChangeHandler = (event) => {
    setpassword(event.target.value);
    setpasswordValid(
      validatefield(
        {
          required: true,
          password: true,
        },
        event.target.value
      )
    );
  };
  const onSubmitLogin = () => {
    console.log(email, password);
    fetch("http://localhost:8080/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        email: email,
        password: password,
      }),
    })
      .then((res) => {
        console.log(res.status);
        return res.json();
      })
      .then((data) => {
        console.log(data);
        if (data.message === "Login successful") {
          localStorage.setItem("token", data.token);
          console.log(localStorage.getItem("token"));
          navigate("/");
        } else {
          setError(data.message);
        }
      })
      .catch((err) => {
        console.log("errrrrrrrrrrr", err);
        setError(err.TypeError);
      });
  };
  return (
    <>
      <Title title="Log in" />
      {error && <Error title={error} />}
      <TextField
        label="Email"
        onChange={onEmailChangeHandler}
        req={true}
        valid={emailValid}
      />
      <TextField
        label="Password"
        onChange={onPasswordChangeHandler}
        req={true}
        valid={passwordValid}
      />
      <Button onClick={onSubmitLogin} title="Log in" />
    </>
  );
}

export default Login;
