import classes from "./Card.module.css";

function Card(props) {
  return (
    <div className="col-md-6 col-lg-3" style={{ "margin-bottom": "30px" }}>
      <div className={`card`}>
        <div className={classes.mycard}>
          <img className={`card-img-top ${classes.img}`} src={props.imageURL} />
        </div>
        <div class="card-body">
          <h5 class="card-title">{props.productname}</h5>
          <p class="card-text">{props.description}</p>
        </div>
        <div class="card-footer">
          <small class="text-muted">
            $ {props.price}
            <button className={classes.addcart}>Add to Cart</button>
          </small>
        </div>
      </div>
    </div>
  );
}

export default Card;
