import { useEffect, useState } from "react";
import classes from "./Reviews.module.css";
import Button from "./UI/Button";
import Review from "./UI/Review";
import { useParams } from "react-router-dom";

const rlist = [
  {
    rating: 5,
    feedback: "Great product, worth the cost",
    userName: "Ashwin",
    date: "20/01/2023",
  },
  {
    rating: 5,
    feedback: "Great product, worth the cost",
    userName: "Ashwin",
    date: "20/01/2023",
  },
  {
    rating: 5,
    feedback: "Great product, worth the cost",
    userName: "Ashwin",
    date: "20/01/2023",
  },
];
function Reviews(props) {
  const params = useParams();
  const [showReviews, setshowReviews] = useState(false);
  const [reviewList, setReviewList] = useState([]);
  const showToggler = () => {
    setshowReviews(!showReviews);
  };

  useEffect(() => {
    console.log("in use effect,", params.productId);
    const url = "http://localhost:8080/getReviews/" + params.productId;
    fetch(url)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log("review data", data);
        setReviewList(data.reviewsList);
      });
  }, []);
  return (
    <div className="container">
      <Button title="Show reviews" onClick={showToggler} />
      {showReviews && (
        <div>
          {reviewList.map((r) => {
            return <Review reviewData={r} />;
          })}
        </div>
      )}
    </div>
  );
}

export default Reviews;
