import classes from "./Authenticate.module.css";
import { useState } from "react";
import TextField from "./UI/TextField";

function Authenticate() {
  const [email, setemail] = useState("");
  const [password, setpassword] = useState("");

  const onEmailChangeHandler = (event) => {
    setemail(event.target.value);
  };
  const onPasswordChangeHandler = (event) => {
    setpassword(event.target.value);
  };
  const onSubmit = () => {
    console.log(email, password);
  };
  return (
    <section className={classes.box}>
      <div className={classes.title}>Log in</div>
      <div>
        <div className={classes.field}>
          <input
            onChange={onEmailChangeHandler}
            className={classes.textfield}
            type="text"
            required
          ></input>
          <label className={classes.label}>
            Email<ion-icon name="mail-outline"></ion-icon>
          </label>
        </div>
        <span className={classes.error}>Error: Invalid Value</span>
      </div>
      <TextField label="Username" />
      <div className={classes.field}>
        <input
          onChange={onPasswordChangeHandler}
          className={classes.textfield}
          type="text"
          required
        ></input>
        <label className={classes.label}>
          Password<ion-icon name="lock-closed-outline"></ion-icon>
        </label>
      </div>
      <button className={classes.button} onClick={onSubmit}>
        Log in
      </button>
      <div className={classes.forget}>
        <label for="">
          <a href="#">Forget Password</a>
        </label>
      </div>

      <div className={classes.toggle}>Don't have account? Register</div>
    </section>
  );
}

export default Authenticate;
