import classes from "./Authenticate.module.css";
import { useState } from "react";
import TextField from "./UI/TextField";

function Authenticate() {
  const [username, setusername] = useState("");
  const [email, setemail] = useState("");
  const [password, setpassword] = useState("");
  const [formLogin, setformLogin] = useState(true);

  const onEmailChangeHandler = (event) => {
    setemail(event.target.value);
  };
  const onPasswordChangeHandler = (event) => {
    setpassword(event.target.value);
  };
  const onUserNameChangeHandler = (event) => {
    setusername(event.target.value);
  };
  const onSubmit = () => {
    console.log(email, password, username);
  };
  const formToggleHandler = () => {
    setformLogin(!formLogin);
  };
  return (
    <section className={classes.box}>
      <div className={classes.title}>{formLogin ? "Log in" : "Sign Up"}</div>
      {/* <div className={classes.fieldbox}>
        <div className={classes.field}>
          <input
            onChange={onEmailChangeHandler}
            className={classes.textfield}
            type="text"
            required
          ></input>
          <label className={classes.label}>
            Email<ion-icon name="mail-outline"></ion-icon>
          </label>
        </div>
        <span className={classes.error}>Error: Invalid Value</span>
      </div> */}
      {!formLogin && (
        <TextField
          label="Username"
          onChange={onUserNameChangeHandler}
          req={true}
        />
      )}
      <TextField label="Email" onChange={onEmailChangeHandler} req={true} />
      <TextField
        label="Password"
        onChange={onPasswordChangeHandler}
        req={true}
      />
      {/* <div className={classes.field}>
        <input
          onChange={onPasswordChangeHandler}
          className={classes.textfield}
          type="text"
          required
        ></input>
        <label className={classes.label}>
          Password<ion-icon name="lock-closed-outline"></ion-icon>
        </label>
      </div> */}
      <button className={classes.button} onClick={onSubmit}>
        Log in
      </button>

      {/* <div className={classes.toggle}> */}
      <button className={classes.button} onClick={formToggleHandler}>
        {formLogin ? "Don't have account? Register" : "Already a user? Log in"}
      </button>
      {/* </div> */}
    </section>
  );
}

export default Authenticate;
