import classes from "./HorizontalCard.module.css";
import { useDispatch, useSelector } from "react-redux";
import { cartActions } from "../../store/index";

function HorizontalCard(props) {
  const dispatch = useDispatch();
  const quantity = useSelector(
    (state) =>
      state.cart.cart.cartList[
        state.cart.cart.cartList.findIndex((obj) => obj.id === props.prod._id)
      ][props.prod._id]
  );
  console.log("quantity", quantity);
  const onAdd = () => {
    const key = props.prod._id;
    dispatch(cartActions.addToCart({ [key]: 1 }));
  };
  const onRemove = () => {
    dispatch(cartActions.removefromcart([props.prod._id, 1]));
  };
  return (
    <div class="container">
      <div
        className={`card mb-3 ${classes.card}`}
        style={{ "max-width": "540px" }}
      >
        <div class="row g-0">
          <div className={`col-md-4 col-sm-4 col-4 ${classes.mycard}`}>
            <img
              src={props.prod.imageURLs[0]}
              className={`img-fluid rounded-start ${classes.img}`}
              alt="..."
            />
          </div>
          <div class="col-md-8 col-sm-8 col-8">
            <div class="card-body">
              <h5 class="card-title">{props.prod.productname}</h5>
              <p class="card-text">{props.prod.description}</p>
              <p class="card-text">
                <small class="text-muted">${props.prod.price}</small>
              </p>
              <button onClick={onAdd}>+</button>
              <span>{quantity}</span>
              <button onClick={onRemove}>-</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default HorizontalCard;
