import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import Review from "./UI/Review";

function ReviewList() {
  const params = useParams();
  const [reviewList, setReviewList] = useState([]);
  useEffect(() => {
    console.log("in use effect,", params.productId);
    const url = "http://localhost:8080/getReviews/" + params.productId;
    fetch(url)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log("review data", data);
        setReviewList(data.reviewsList);
      });
  }, []);
  return (
    <div>
      {reviewList.map((r) => {
        return <Review reviewData={r} />;
      })}
      <div class="toast" data-autohide="false">
        <div class="toast-header">
          <strong class="mr-auto text-primary">Toast Header</strong>
          <small class="text-muted">5 mins ago</small>
          <button type="button" class="ml-2 mb-1 close" data-dismiss="toast">
            &times;
          </button>
        </div>
        <div class="toast-body">Some text inside the toast body</div>
      </div>
    </div>
  );
}

export default ReviewList;
