import classes from "./Reviews.module.css";
import Button from "./UI/Button";

const rlist = [
  {
    rating: 5,
    feedback: "Great product, worth the cost",
    userName: "Ashwin",
    date: "20/01/2023",
  },
  {
    rating: 5,
    feedback: "Great product, worth the cost",
    userName: "Ashwin",
    date: "20/01/2023",
  },
  {
    rating: 5,
    feedback: "Great product, worth the cost",
    userName: "Ashwin",
    date: "20/01/2023",
  },
];
function Reviews() {
  let arr = [];
  for (var i = 0; i < props.rating; i++) {
    arr.push(<ion-icon className={classes.star} name="star"></ion-icon>);
  }
  return (
    <div className="container">
      <Button title="Show reviews" />
      <div>
        <div>
          {rlist.map((r) => {
            return (
              <div className="container">
                <div className={classes.review}>
                  <div className={classes.name}>
                    <div className={classes.username}>{r.userName}</div>
                    <div className={classes.date}>{r.date}</div>
                  </div>
                  <div></div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default Reviews;
